import plotly.graph_objects as go

fig = go.Figure()

fig.add_trace(
  go.Scatter(
    mode = 'markers', # line, markers, text
    x = [1,2,3],
    y = [10,20,30],
    marker=dict(
        color='LightSkyBlue',
        size=20,
        line=dict(
            color='MediumPurple',
            width=2)
      ),
    name= 'Marker Style 1'
  )
)

fig.add_trace(
  go.Scatter(
    mode = 'lines',
    x = [1,2,3],
    y = [30,20,10],
    line=dict(
      color='Blue',
      width=5
    ),
    name= 'Marker Style 2'
  )
)

fig.add_trace(
