import plotly.graph_objects as go

fig = go.Figure()

fig.add_shape(type="line",
    x0=1, y0=1, x1=5, y1=1,
    opacity=0.5,
    line=dict(
        color="MediumPurple",
        width=4,
        dash="dot",
    )
)

fig.add_shape(type="line",
    x0=1, y0=2, x1=5, y1=2,
    opacity=0.7,
    line=dict(
        color="MediumPurple",
        width=4,
        dash="dot",
    )
)

# Set axes ranges
fig.update_xaxes(range=[0, 6])
fig.update_yaxes(range=[0, 3])

fig.show()
